import '../styles/components/form.scss';
import FormDesign from './FormDesign';
import FormShare from './FormShare';
import FormFill from './FormFill';

const Form = (props) => {
  //pendiente coger los props de App.js
  const handleSubmit = (ev) => {
    ev.preventDefault();
  };
  const [collapsableDesign, setCollapsableDesign] = useState('hidden');
  const [collapsableFill, setCollapsableFill] = useState('hidden');
  const [collapsableShare, setCollapsableShare] = useState('hidden');
  const [arrow, setArrow] = useState('fa-chevron-up');
  const [arrow2, setArrow2] = useState('fa-chevron-down');
  const [arrow3, setArrow3] = useState('fa-chevron-down');



  return (
    <section className="section_form">
      <form onSubmit={handleSubmit} action="#" className="form js_form">
        <FormDesign
          handlerCollapsableDesign={props.handlerCollapsableDesign}
          arrow={props.arrow}
          collapsableDesign={props.collapsableDesign}
          handleInput={props.handleInput}
        />
        <FormFill handlerCollapsableFill={props.handleCollapsableFill}
          arrow={props.arrow2}
          collapsableFill={props.collapsableFill}
          handleInput={props.handleInput}
          data={props.data}
          />

        <FormShare
          handlerCollapsableShare={props.handlerCollapsableShare}
          arrow3={props.arrow3}
          collapsableShare={props.collapsableShare}
        />
      </form>
    </section>
  );
};

export default Form;
