// Fichero src/components/App.js
import { useState } from 'react';
import '../styles/app.scss';
import Header from './Header';
import Preview from './Preview';
import Form from './Form';
import Footer from './Footer';

function App() {
  const [collapsableDesign, setCollapsableDesign] = useState('hidden');
  const [collapsableFill, setCollapsableFill] = useState('hidden');
  const [collapsableShare, setCollapsableShare] = useState('hidden');
  const [arrow, setArrow] = useState('fa-chevron-up');
  const [arrow2, setArrow2] = useState('fa-chevron-down');
  const [arrow3, setArrow3] = useState('fa-chevron-down');

  const [data, setData] = useState({
    palette: 'palette1',
    name: '',
    job: '',
    image: '',
    phone: '',
    email: '',
    linkedin: '',
    github: '',
  });

  const handlerCollapsableDesign = (ev) => {
    ev.preventDefault();
    if (collapsableDesign === 'hidden') {
      setCollapsableDesign('');
      setArrow('fa-chevron-up');
    } else {
      setCollapsableDesign('hidden');
      setArrow('fa-chevron-down');
      setCollapsableFill('');
      setArrow2('fa-chevron-up');
    }

    setCollapsableShare('hidden');

    setArrow3('fa-chevron-down');
  };

  const handlerCollapsableFill = (ev) => {
    ev.preventDefault();
    if (collapsableFill === 'hidden') {
      setCollapsableFill('');
      setArrow2('fa-chevron-up');
    } else {
      setCollapsableFill('hidden');
      setArrow2('fa-chevron-down');
      setCollapsableShare('');
      setArrow3('fa-chevron-up');
    }
    setCollapsableDesign('hidden');

    setArrow('fa-chevron-down');
  };

  const handlerCollapsableShare = (ev) => {
    ev.preventDefault();
    if (collapsableShare === 'hidden') {
      setCollapsableShare('');
      setArrow3('fa-chevron-up');
    } else {
      setCollapsableShare('hidden');
      setArrow3('fa-chevron-down');
    }
    setCollapsableDesign('hidden');
    setCollapsableFill('hidden');
    setArrow('fa-chevron-down');
    setArrow2('fa-chevron-down');
  };

  const handleInput = (targetName, targetValue) => {


	if (targetName === 'name') {
	  setData({
		...data,
		name: targetValue,
	  });
	} else if (targetName === 'job') {
	  setData({
		...data,
		job: targetValue,
	  });
	} else if (targetName === 'phone') {
	  setData({
		...data,
		phone: targetValue,
	  });
	} else if (targetName === 'email') {
	  setData({
		...data,
		email: targetValue,
	  });
	} else if (targetName === 'linkedin') {
	  setData({
		...data,
		linkedin: targetValue,
	  });
	} else if (targetName === 'github') {
	  setData({
		...data,
		github: targetValue,
	  });
    } else if (targetName === 'color_palette') {
      setData({
        ...data,
        palette: targetValue,
      });
    }
  };



  return (
    <div className="page">
      	<Header />
      	<main className="main_grid">
        <Preview data={data} />
        <Form data={data} handlerCollapsableDesign={handlerCollapsableDesign} handlerCollapsableFill={handlerCollapsableFill} handlerCollapsableShare={handlerCollapsableShare} handleInput={handleInput}/>   
		</main>     
      	<Footer />
    </div>
  );
  }

export default App;
